init("Menu bijouterie");

function init(title){
    document.getElementById("pageTitle").innerHTML = title;
    var shopContainer = document.getElementById("shopContainer")
    for (var key in currentlist){
        var slot = document.createElement("div");
        slot.classList.add("slot");
        slot.dataset.name = key;
        slot.innerHTML = `<img src="assets/svg/${currentlist[key].icon}">`;
        
        slot.addEventListener("click", function(e){
            e.stopPropagation();
            if(e.currentTarget.classList.contains("activeslot")){
                e.currentTarget.classList.remove("activeslot");
                destroyPanier();
            }
            else{
                var ss = document.getElementsByClassName("activeslot");
                for(var i=0; i<ss.length; i++) ss[i].classList.remove("activeslot");
                e.currentTarget.classList.add("activeslot");
                destroyPanier();
                panier(e.currentTarget.dataset.name);
            }
        });

        shopContainer.appendChild(slot);
    }
}


function destroyPanier(){
    document.getElementById("panier").style.display = "none";
    document.getElementById("panier").innerHTML = "";
}


function registerslider(e){
    var key = e.currentTarget.dataset.key;
    var sliderkey = e.currentTarget.dataset.sliderkey;

    var category = currentlist[key];
    var slidersKeys = Object.keys(category.sliders);
    var sliderIndex = slidersKeys.indexOf(sliderkey)
    var slider = category.sliders[sliderkey];
    var firstSlider = category.sliders[slidersKeys[0]]
    var firstCurrentIndex = firstSlider.indexOf(category.current[0]);
    
    if(sliderIndex == 0){
        category.current[sliderIndex] = currentlist[key].sliders[sliderkey][e.currentTarget.value-1];
        for (var i=1; i<slidersKeys.length; i++){
            var skey = slidersKeys[i];
            var sliderMax = category.sliders[skey][firstSlider.indexOf(category.current[sliderIndex])].length;
            document.getElementById(`slider_${skey}`).max = sliderMax;
            document.getElementById(`slider_${skey}`).value = Math.min(document.getElementById(`slider_${skey}`).value, document.getElementById(`slider_${skey}`).max);
            document.getElementById("slidertext_"+skey).innerHTML = document.getElementById(`slider_${skey}`).value;

            category.current[i] = category.sliders[skey][firstCurrentIndex][document.getElementById(`slider_${skey}`).value-1];
        }
    }
    else category.current[sliderIndex] = slider[firstCurrentIndex][e.currentTarget.value-1];
    
    document.getElementById("slidertext_"+sliderkey).innerHTML = e.currentTarget.value;
}


function panier(key){
    document.getElementById("panier").innerHTML = `<div class="containerTitle">${key}</div><div id="panierContent" class="containerContents"></div>`;

    var category = currentlist[key];

    for (var sliderkey in currentlist[key].sliders){
        var slidersKeys = Object.keys(category.sliders);
        var sliderIndex = slidersKeys.indexOf(sliderkey)
        var slider = category.sliders[sliderkey];
        var firstSlider = category.sliders[slidersKeys[0]]
        var currentVal = category.current[sliderIndex];
        var firstCurrentIndex = firstSlider.indexOf(category.current[0]);

        var sliderValue = sliderIndex == 0 ? slider.indexOf(currentVal) + 1 : slider[firstCurrentIndex].indexOf(currentVal) + 1;
        var sliderMax = sliderIndex == 0 ? slider.length : slider[firstCurrentIndex].length;

        document.getElementById("panierContent").innerHTML += `<div class="box">
            <div class="boxTitle">${sliderkey}</div>
            <div class="sliderBox">
                <div id="slidertext_${sliderkey}" class="sliderValue">${sliderValue}</div>
                <input data-key="${key}" data-sliderkey="${sliderkey}" oninput="registerslider(event)" id="slider_${sliderkey}" class="slider" type="range" min="1" max="${sliderMax}" value="${sliderValue}" />
            </div>
        </div>`;
    }

    document.getElementById("panierContent").innerHTML += `<div class="box buttonBox">
        <div id="addButton" class="button">Add ${currentlist[key].prix} $</div>
        <div id="clearButton" class="button">Clear</div>
    </div>
    <div class="box buttonBox" id="acheterButtonBox">
        <div id="titlePanier">Panier ${panierTotal} $</div>
        <img id="logoPanier" src="assets/svg/shopping-cart.svg">
    </div>`;

    document.getElementById("addButton").addEventListener('click', function(e){
        panierTotal += currentlist[key].prix;
        document.getElementById('titlePanier').innerHTML = `Panier ${panierTotal} $`;

        shoppingCart.push(Object.assign({}, {
            id: currentlist[key].id,
            accessory: currentlist[key].accessory,
            current: currentlist[key].current
        }));
    });

    document.getElementById("clearButton").addEventListener('click', function(e){
        panierTotal = 0;
        shoppingCart = [];
        document.getElementById('titlePanier').innerHTML = `Panier ${panierTotal} $`;
    });

    document.getElementById("acheterButtonBox").addEventListener('click', function(e){
        console.log(shoppingCart);
        if(panierTotal != 0){
            document.getElementById("checkout").play();
            panierTotal = 0;
            shoppingCart = [];
            document.getElementById('titlePanier').innerHTML = `Panier ${panierTotal} $`;
        }
    });

    document.getElementById("panier").style.display = "block";
}
